// src/pages/PaymentMethods.jsx
import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { CreditCard, PlusCircle, Edit, Trash2, Loader2 } from "lucide-react";
import {
  fetchUserPaymentMethods,
  addPaymentMethod,
  updatePaymentMethod,
  deletePaymentMethod,
} from "../api/userApi"; // userApi.js'nin doğru yolu

const PaymentMethods = () => {
  const [paymentMethods, setPaymentMethods] = useState([]);
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingMethod, setEditingMethod] = useState(null);
  const [newMethod, setNewMethod] = useState({
    cardType: "",
    cardNumber: "", // Tam kart numarasını almak için yeni alan
    expiry: "",
    cvc: "",         // CVC/CVV almak için yeni alan (sadece ekleme formunda)
    isDefault: false,
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [submitting, setSubmitting] = useState(false);

  // In a real application, you'd get the token from an authentication context
  // or a more secure storage mechanism. For this example, we'll get it from localStorage.
  const token = localStorage.getItem("token"); // Assuming token is stored in localStorage

  const getPaymentMethods = async () => {
    if (!token) {
      setError("Authentication token not found. Please log in.");
      setLoading(false);
      return;
    }
    setLoading(true);
    setError(null);
    try {
      const data = await fetchUserPaymentMethods(token);
      // API'nizden gelen veriye göre uyarlayın.
      // Eğer backend'iniz 'lastFour' haneyi kendisi hesaplıyorsa,
      // bu kısmı olduğu gibi bırakabilirsiniz.
      // Yoksa, her kart için 'lastFour' alanını manuel olarak oluşturmalısınız:
      const formattedData = data.map(card => ({
        ...card,
        lastFour: card.cardNumber ? card.cardNumber.slice(-4) : card.lastFour // Kart numarası varsa son 4 hanesini al
      }));
      setPaymentMethods(formattedData);

    } catch (err) {
      console.error("Failed to fetch payment methods:", err);
      setError(err.message || "Ödeme yöntemleri alınamadı.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    getPaymentMethods();
  }, [token]);

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    let formattedValue = value;

    // Kart numarası için sadece rakamları al ve boşluk ekle
    if (name === "cardNumber") {
      formattedValue = value.replace(/\D/g, '').replace(/(.{4})/g, '$1 ').trim();
      if (formattedValue.length > 19) { // 16 hane + 3 boşluk = 19 karakter
        formattedValue = formattedValue.substring(0, 19);
      }
    }
    // Son kullanma tarihi için AA/YY formatı
    if (name === "expiry") {
      formattedValue = value.replace(/\D/g, ''); // Sadece rakamlar
      if (formattedValue.length > 2) {
        formattedValue = formattedValue.substring(0, 2) + '/' + formattedValue.substring(2, 4);
      }
      if (formattedValue.length > 5) { // AA/YY formatı (5 karakter)
        formattedValue = formattedValue.substring(0, 5);
      }
    }
    // CVC için sadece rakamlar ve max 4 karakter
    if (name === "cvc") {
      formattedValue = value.replace(/\D/g, '').substring(0, 4);
    }

    setNewMethod({
      ...newMethod,
      [name]: type === "checkbox" ? checked : formattedValue,
    });
  };

  const handleAddOrUpdateMethod = async (e) => {
    e.preventDefault();
    if (!token) {
      setError("Authentication token not found. Please log in.");
      return;
    }

    setSubmitting(true);
    setError(null);

    // Backend'e gönderilecek veri: tam kart numarası ve diğer bilgiler
    const dataToSend = {
      cardType: newMethod.cardType,
      cardNumber: newMethod.cardNumber.replace(/\s/g, ''), // Boşlukları kaldır
      expiry: newMethod.expiry,
      // CVC sadece ekleme sırasında gönderilir, depolanmaz veya güncellenmez
      ...(editingMethod ? {} : { cvc: newMethod.cvc }),
      isDefault: newMethod.isDefault,
    };

    try {
      if (editingMethod) {
        // Kart güncelleme durumunda CVC gönderilmez.
        // Backend'in hangi alanların güncellenmesine izin verdiğine bağlı olarak
        // cardNumber'ı da güncellemek isteyebilirsiniz, ancak genellikle kart numarası güncellenmez.
        // Bu örnekte sadece isDefault ve cardType güncellenebilir varsayıyoruz.
        const updateData = {
            cardType: newMethod.cardType,
            expiry: newMethod.expiry,
            isDefault: newMethod.isDefault,
            // cardNumber'ı güncelleme API'sine göndermemek daha güvenlidir.
            // Eğer backendde destekleniyorsa ve gerekliyse ekleyebilirsiniz.
        };
        await updatePaymentMethod(editingMethod.id, updateData, token);
      } else {
        // Yeni ödeme yöntemi ekleme
        await addPaymentMethod(dataToSend, token);
      }
      setNewMethod({ cardType: "", cardNumber: "", expiry: "", cvc: "", isDefault: false });
      setShowAddForm(false);
      setEditingMethod(null);
      await getPaymentMethods(); // Refresh the list
    } catch (err) {
      console.error("Failed to save payment method:", err);
      setError(err.message || "Ödeme yöntemi kaydedilirken bir hata oluştu.");
    } finally {
      setSubmitting(false);
    }
  };

  const handleEditMethod = (method) => {
    setEditingMethod(method);
    // Düzenleme modunda, tam kart numarasını veya CVC'yi göstermemeliyiz.
    // Bu alanları boş bırakarak kullanıcıdan tekrar girmesini beklemek en güvenlisidir.
    setNewMethod({
      cardType: method.cardType,
      cardNumber: "", // Düzenlerken tam kart numarasını gösterme
      expiry: method.expiry,
      cvc: "", // Düzenlerken CVC'yi gösterme
      isDefault: method.isDefault,
    });
    setShowAddForm(true);
  };

  const handleDeleteMethod = async (id) => {
    if (!token) {
      setError("Authentication token not found. Please log in.");
      return;
    }

    if (window.confirm("Bu ödeme yöntemini silmek istediğinize emin misiniz?")) {
      setSubmitting(true);
      setError(null);
      try {
        await deletePaymentMethod(id, token);
        await getPaymentMethods(); // Refresh the list
      } catch (err) {
        console.error("Failed to delete payment method:", err);
        setError(err.message || "Ödeme yöntemi silinirken bir hata oluştu.");
      } finally {
        setSubmitting(false);
      }
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-white p-6">
      <div className="container mx-auto max-w-3xl bg-white dark:bg-gray-800 rounded-lg shadow-md p-8">
        <h2 className="text-3xl font-bold mb-6 text-center">Ödeme Yöntemlerim</h2>

        <div className="flex justify-end mb-6">
          <button
            onClick={() => {
              setShowAddForm(!showAddForm);
              setEditingMethod(null); // Close editing mode when adding new
              setNewMethod({ cardType: "", cardNumber: "", expiry: "", cvc: "", isDefault: false }); // Reset form
            }}
            className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg flex items-center transition-colors"
            disabled={submitting}
          >
            <PlusCircle size={20} className="mr-2" />
            {showAddForm ? "İptal" : "Yeni Ödeme Yöntemi Ekle"}
          </button>
        </div>

        {error && (
          <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4" role="alert">
            <strong className="font-bold">Hata!</strong>
            <span className="block sm:inline"> {error}</span>
          </div>
        )}

        {showAddForm && (
          <motion.form
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
            onSubmit={handleAddOrUpdateMethod}
            className="bg-gray-50 dark:bg-gray-700 p-6 rounded-lg mb-8 shadow-inner"
          >
            <h3 className="text-xl font-semibold mb-4">
              {editingMethod ? "Ödeme Yöntemini Düzenle" : "Yeni Ödeme Yöntemi Ekle"}
            </h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
              <div>
                <label htmlFor="cardType" className="block text-sm font-medium mb-1">
                  Kart Tipi (örn: Visa, MasterCard)
                </label>
                <input
                  type="text"
                  id="cardType"
                  name="cardType"
                  value={newMethod.cardType}
                  onChange={handleInputChange}
                  className="w-full p-2 border border-gray-300 dark:border-gray-600 rounded-md bg-white dark:bg-gray-900 text-gray-900 dark:text-white"
                  required
                  disabled={submitting}
                />
              </div>
              <div>
                <label htmlFor="cardNumber" className="block text-sm font-medium mb-1">
                  Kart Numarası
                </label>
                <input
                  type="text"
                  id="cardNumber"
                  name="cardNumber"
                  value={newMethod.cardNumber}
                  onChange={handleInputChange}
                  placeholder="XXXX XXXX XXXX XXXX"
                  className="w-full p-2 border border-gray-300 dark:border-gray-600 rounded-md bg-white dark:bg-gray-900 text-gray-900 dark:text-white"
                  required
                  disabled={submitting || editingMethod} // Düzenlerken kart numarasını değiştirmeyi engelle
                />
              </div>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
              <div>
                <label htmlFor="expiry" className="block text-sm font-medium mb-1">
                  Son Kullanma Tarihi (AA/YY)
                </label>
                <input
                  type="text"
                  id="expiry"
                  name="expiry"
                  maxLength={5} // MM/YY
                  value={newMethod.expiry}
                  onChange={handleInputChange}
                  placeholder="AA/YY"
                  className="w-full p-2 border border-gray-300 dark:border-gray-600 rounded-md bg-white dark:bg-gray-900 text-gray-900 dark:text-white"
                  required
                  disabled={submitting}
                />
              </div>
              <div>
                <label htmlFor="cvc" className="block text-sm font-medium mb-1">
                  CVC/CVV
                </label>
                <input
                  type="text"
                  id="cvc"
                  name="cvc"
                  maxLength={4}
                  value={newMethod.cvc}
                  onChange={handleInputChange}
                  className="w-full p-2 border border-gray-300 dark:border-gray-600 rounded-md bg-white dark:bg-gray-900 text-gray-900 dark:text-white"
                  required={!editingMethod} // Ekleme yaparken zorunlu, düzenlerken değil
                  disabled={submitting || editingMethod} // Düzenlerken CVC almayı engelle
                />
              </div>
            </div>
            <div className="flex items-center mb-4">
              <input
                type="checkbox"
                id="isDefault"
                name="isDefault"
                checked={newMethod.isDefault}
                onChange={handleInputChange}
                className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
                disabled={submitting}
              />
              <label htmlFor="isDefault" className="ml-2 block text-sm font-medium">
                Varsayılan Ödeme Yöntemi Yap
              </label>
            </div>
            <button
              type="submit"
              className="w-full bg-green-600 hover:bg-green-700 text-white font-bold py-2 px-4 rounded-lg transition-colors flex items-center justify-center"
              disabled={submitting}
            >
              {submitting && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
              {editingMethod ? "Yöntemi Güncelle" : "Yöntemi Kaydet"}
            </button>
          </motion.form>
        )}

        {loading ? (
          <div className="flex justify-center items-center h-48">
            <Loader2 className="h-8 w-8 animate-spin text-blue-500" />
            <p className="ml-3 text-lg text-gray-600 dark:text-gray-400">Ödeme yöntemleri yükleniyor...</p>
          </div>
        ) : paymentMethods.length === 0 ? (
          <p className="text-center text-gray-600 dark:text-gray-400 text-lg">
            Henüz kayıtlı ödeme yönteminiz bulunmamaktadır.
          </p>
        ) : (
          <div className="space-y-4">
            {paymentMethods.map((method) => (
              <div
                key={method.id}
                className="bg-white dark:bg-gray-700 border border-gray-200 dark:border-gray-600 rounded-lg p-5 shadow-sm flex items-start justify-between"
              >
                <div className="flex items-start">
                  <CreditCard size={24} className="text-blue-500 mr-4 flex-shrink-0 mt-1" />
                  <div>
                    <h3 className="text-xl font-semibold mb-1 flex items-center">
                      {method.cardType} **** {method.lastFour} {/* Son dört haneyi göster */}
                      {method.isDefault && (
                        <span className="ml-2 text-xs bg-blue-100 text-blue-800 dark:bg-blue-800 dark:text-blue-100 px-2 py-1 rounded-full">
                          Varsayılan
                        </span>
                      )}
                    </h3>
                    <p className="text-gray-700 dark:text-gray-300">Son Kullanma: {method.expiry}</p>
                  </div>
                </div>
                <div className="flex space-x-2">
                  <button
                    onClick={() => handleEditMethod(method)}
                    className="p-2 rounded-full hover:bg-blue-100 dark:hover:bg-blue-700 transition-colors"
                    title="Düzenle"
                    disabled={submitting}
                  >
                    <Edit size={20} className="text-blue-500" />
                  </button>
                  <button
                    onClick={() => handleDeleteMethod(method.id)}
                    className="p-2 rounded-full hover:bg-red-100 dark:hover:bg-red-700 transition-colors"
                    title="Sil"
                    disabled={submitting}
                  >
                    <Trash2 size={20} className="text-red-500" />
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default PaymentMethods;
