import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const OrderHistory = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    fetch("/api/orders/myorders", {
      credentials: "include",
    })
      .then((res) => res.json())
      .then((data) => {
        setOrders(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Sipariş geçmişi alınırken hata:", err);
        setLoading(false);
      });
  }, []);

  const formatDate = (dateStr) => {
    const date = new Date(dateStr);
    return date.toLocaleDateString("tr-TR", {
      year: "numeric",
      month: "long",
      day: "numeric",
    });
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white p-6">
      <h1 className="text-3xl font-bold mb-6">Sipariş Geçmişi</h1>

      {loading ? (
        <p>Yükleniyor...</p>
      ) : orders.length === 0 ? (
        <p>Henüz bir siparişiniz yok.</p>
      ) : (
        <div className="grid gap-6">
          {orders.map((order) => (
            <div
              key={order._id}
              className="bg-gray-800 rounded-xl shadow-md p-4 hover:ring-2 hover:ring-green-400 transition-all"
            >
              <div className="flex justify-between items-center mb-2">
                <div>
                  <p className="text-lg font-semibold">Sipariş No: {order._id}</p>
                  <p className="text-gray-400 text-sm">{formatDate(order.createdAt)}</p>
                </div>
                <div className="text-right">
                  <p className="text-sm text-gray-300">
                    Ürün Sayısı: {order.orderItems?.length || 0}
                  </p>
                  <p className="text-sm text-gray-300">
                    Toplam: ₺{order.totalPrice?.toFixed(2)}
                  </p>
                  <p
                    className={`text-sm font-semibold ${
                      order.isDelivered ? "text-green-400" : "text-yellow-400"
                    }`}
                  >
                    {order.isDelivered ? "Teslim Edildi" : "Kargoda"}
                  </p>
                </div>
              </div>
              <button
                onClick={() => navigate(`/order/${order._id}`)}
                className="mt-2 inline-block text-sm text-green-400 hover:underline"
              >
                Detayları Görüntüle
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default OrderHistory;
