// src/pages/Addresses.jsx
import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import { MapPin, PlusCircle, Edit, Trash2 } from "lucide-react";

// Varsayımsal adres verileri
const initialAddresses = [
  {
    id: "1",
    title: "Ev Adresi",
    address: "Örnek Mah. Deneme Cad. No:1 D:1, Örnek İlçe, Örnek Şehir",
    phone: "555 123 4567",
    isDefault: true,
  },
  {
    id: "2",
    title: "İş Adresi",
    address: "İş Merkezi Sk. Ticaret Cad. No:10, İş İlçe, İş Şehir",
    phone: "555 987 6543",
    isDefault: false,
  },
];

const Addresses = () => {
  const [addresses, setAddresses] = useState(initialAddresses);
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingAddress, setEditingAddress] = useState(null); // Düzenlenen adresin ID'si veya objesi
  const [newAddress, setNewAddress] = useState({
    title: "",
    address: "",
    phone: "",
    isDefault: false,
  });

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setNewAddress({
      ...newAddress,
      [name]: type === "checkbox" ? checked : value,
    });
  };

  const handleAddOrUpdateAddress = (e) => {
    e.preventDefault();
    if (editingAddress) {
      // Adres güncelleme
      setAddresses(
        addresses.map((addr) =>
          addr.id === editingAddress.id ? { ...newAddress, id: editingAddress.id } : addr
        )
      );
      setEditingAddress(null);
    } else {
      // Yeni adres ekleme
      setAddresses([
        ...addresses,
        { ...newAddress, id: String(addresses.length + 1) },
      ]);
    }
    setNewAddress({ title: "", address: "", phone: "", isDefault: false });
    setShowAddForm(false);
  };

  const handleEditAddress = (address) => {
    setEditingAddress(address);
    setNewAddress(address);
    setShowAddForm(true);
  };

  const handleDeleteAddress = (id) => {
    if (window.confirm("Bu adresi silmek istediğinize emin misiniz?")) {
      setAddresses(addresses.filter((addr) => addr.id !== id));
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-white p-6">
      <div className="container mx-auto max-w-3xl bg-white dark:bg-gray-800 rounded-lg shadow-md p-8">
        <h2 className="text-3xl font-bold mb-6 text-center">Adreslerim</h2>

        <div className="flex justify-end mb-6">
          <button
            onClick={() => {
              setShowAddForm(!showAddForm);
              setEditingAddress(null); // Yeni adres eklerken düzenleme modunu kapat
              setNewAddress({ title: "", address: "", phone: "", isDefault: false });
            }}
            className="bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg flex items-center transition-colors"
          >
            <PlusCircle size={20} className="mr-2" />
            {showAddForm ? "İptal" : "Yeni Adres Ekle"}
          </button>
        </div>

        {showAddForm && (
          <motion.form
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
            onSubmit={handleAddOrUpdateAddress}
            className="bg-gray-50 dark:bg-gray-700 p-6 rounded-lg mb-8 shadow-inner"
          >
            <h3 className="text-xl font-semibold mb-4">
              {editingAddress ? "Adresi Düzenle" : "Yeni Adres Ekle"}
            </h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-4">
              <div>
                <label htmlFor="title" className="block text-sm font-medium mb-1">
                  Adres Başlığı (örn: Ev, İş)
                </label>
                <input
                  type="text"
                  id="title"
                  name="title"
                  value={newAddress.title}
                  onChange={handleInputChange}
                  className="w-full p-2 border border-gray-300 dark:border-gray-600 rounded-md bg-white dark:bg-gray-900 text-gray-900 dark:text-white"
                  required
                />
              </div>
              <div>
                <label htmlFor="phone" className="block text-sm font-medium mb-1">
                  Telefon Numarası
                </label>
                <input
                  type="text"
                  id="phone"
                  name="phone"
                  value={newAddress.phone}
                  onChange={handleInputChange}
                  className="w-full p-2 border border-gray-300 dark:border-gray-600 rounded-md bg-white dark:bg-gray-900 text-gray-900 dark:text-white"
                  required
                />
              </div>
            </div>
            <div className="mb-4">
              <label htmlFor="address" className="block text-sm font-medium mb-1">
                Açık Adres
              </label>
              <textarea
                id="address"
                name="address"
                value={newAddress.address}
                onChange={handleInputChange}
                rows="3"
                className="w-full p-2 border border-gray-300 dark:border-gray-600 rounded-md bg-white dark:bg-gray-900 text-gray-900 dark:text-white"
                required
              ></textarea>
            </div>
            <div className="flex items-center mb-4">
              <input
                type="checkbox"
                id="isDefault"
                name="isDefault"
                checked={newAddress.isDefault}
                onChange={handleInputChange}
                className="h-4 w-4 text-blue-600 focus:ring-blue-500 border-gray-300 rounded"
              />
              <label htmlFor="isDefault" className="ml-2 block text-sm font-medium">
                Varsayılan Adres Yap
              </label>
            </div>
            <button
              type="submit"
              className="w-full bg-green-600 hover:bg-green-700 text-white font-bold py-2 px-4 rounded-lg transition-colors"
            >
              {editingAddress ? "Adresi Güncelle" : "Adresi Kaydet"}
            </button>
          </motion.form>
        )}

        {addresses.length === 0 ? (
          <p className="text-center text-gray-600 dark:text-gray-400 text-lg">
            Henüz kayıtlı adresiniz bulunmamaktadır.
          </p>
        ) : (
          <div className="space-y-4">
            {addresses.map((addr) => (
              <div
                key={addr.id}
                className="bg-white dark:bg-gray-700 border border-gray-200 dark:border-gray-600 rounded-lg p-5 shadow-sm flex items-start justify-between"
              >
                <div className="flex items-start">
                  <MapPin size={24} className="text-blue-500 mr-4 flex-shrink-0 mt-1" />
                  <div>
                    <h3 className="text-xl font-semibold mb-1 flex items-center">
                      {addr.title}
                      {addr.isDefault && (
                        <span className="ml-2 text-xs bg-blue-100 text-blue-800 dark:bg-blue-800 dark:text-blue-100 px-2 py-1 rounded-full">
                          Varsayılan
                        </span>
                      )}
                    </h3>
                    <p className="text-gray-700 dark:text-gray-300">{addr.address}</p>
                    <p className="text-gray-600 dark:text-gray-400 text-sm">Tel: {addr.phone}</p>
                  </div>
                </div>
                <div className="flex space-x-2">
                  <button
                    onClick={() => handleEditAddress(addr)}
                    className="p-2 rounded-full hover:bg-blue-100 dark:hover:bg-blue-700 transition-colors"
                    title="Düzenle"
                  >
                    <Edit size={20} className="text-blue-500" />
                  </button>
                  <button
                    onClick={() => handleDeleteAddress(addr.id)}
                    className="p-2 rounded-full hover:bg-red-100 dark:hover:bg-red-700 transition-colors"
                    title="Sil"
                  >
                    <Trash2 size={20} className="text-red-500" />
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Addresses;
