// frontend/pages/Payment.jsx
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useCart } from "../context/CartContext";
import { useAuth } from "../context/AuthContext"; // AuthContext'i dahil et
import { createOrder } from "../api/orderApi"; // Yeni oluşturduğumuz orderApi'yi dahil et
import { useForm } from "react-hook-form";

const Payment = () => {
  const { cartItems, total, clearCart } = useCart();
  const { token } = useAuth(); // AuthContext'ten token'ı al
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();

  // Sepet boşsa doğrudan ana sayfaya yönlendir
  if (cartItems.length === 0) {
    // navigate("/", { replace: true }); // Ödeme sayfasında kart boşsa direk yönlendir.
    // return null; // Component'in render etmesini engelle
  }


  const onSubmit = async (data) => {
    setLoading(true);
    setErrorMessage("");

    if (!token) {
      setErrorMessage("Sipariş oluşturmak için lütfen giriş yapın.");
      setLoading(false);
      return;
    }

    try {
      const orderData = {
        products: cartItems.map((item) => ({
          product: item._id, // Backend'deki Order modeline göre product ID'si
          quantity: item.quantity,
          price: item.discountPrice || item.price,
        })),
        shippingInfo: {
          address: data.address,
          city: data.city,
          postalCode: data.postalCode,
          country: data.country,
        },
      };

      const createdOrder = await createOrder(orderData, token); // createOrder fonksiyonunu çağır
      clearCart(); // Sipariş başarıyla oluşturulduktan sonra sepeti temizle
      navigate("/order-success", {
        state: {
          orderId: createdOrder._id, // Backend'den dönen sipariş ID'sini gönder
          totalPrice: createdOrder.totalPrice, // Backend'den dönen toplam fiyatı gönder
        },
      });
    } catch (error) {
      console.error("Sipariş oluşturma hatası:", error);
      setErrorMessage(error.message || "Sipariş oluşturulurken bir hata oluştu.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white p-6 flex justify-center items-center">
      <div className="bg-gray-800 p-8 rounded-xl shadow-lg w-full max-w-md">
        <h1 className="text-3xl font-bold mb-6 text-center">Ödeme Bilgileri</h1>

        {cartItems.length === 0 ? (
          <p className="text-center text-red-400">Sepetiniz boş. Lütfen önce ürün ekleyin.</p>
        ) : (
          <>
            <div className="mb-4 text-center">
              <h2 className="text-xl font-semibold text-green-400">
                Toplam Tutar: ₺{total.toFixed(2)}
              </h2>
            </div>

            <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
              <div>
                <label htmlFor="address" className="block text-sm font-medium text-gray-300">
                  Adres
                </label>
                <input
                  id="address"
                  type="text"
                  {...register("address", { required: "Adres zorunludur." })}
                  className="mt-1 block w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-md shadow-sm focus:outline-none focus:ring-green-500 focus:border-green-500 text-white"
                />
                {errors.address && (
                  <p className="mt-1 text-sm text-red-400">{errors.address.message}</p>
                )}
              </div>
              <div>
                <label htmlFor="city" className="block text-sm font-medium text-gray-300">
                  Şehir
                </label>
                <input
                  id="city"
                  type="text"
                  {...register("city", { required: "Şehir zorunludur." })}
                  className="mt-1 block w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-md shadow-sm focus:outline-none focus:ring-green-500 focus:border-green-500 text-white"
                />
                {errors.city && (
                  <p className="mt-1 text-sm text-red-400">{errors.city.message}</p>
                )}
              </div>
              <div>
                <label htmlFor="postalCode" className="block text-sm font-medium text-gray-300">
                  Posta Kodu
                </label>
                <input
                  id="postalCode"
                  type="text"
                  {...register("postalCode", { required: "Posta kodu zorunludur." })}
                  className="mt-1 block w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-md shadow-sm focus:outline-none focus:ring-green-500 focus:border-green-500 text-white"
                />
                {errors.postalCode && (
                  <p className="mt-1 text-sm text-red-400">{errors.postalCode.message}</p>
                )}
              </div>
              <div>
                <label htmlFor="country" className="block text-sm font-medium text-gray-300">
                  Ülke
                </label>
                <input
                  id="country"
                  type="text"
                  {...register("country", { required: "Ülke zorunludur." })}
                  className="mt-1 block w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-md shadow-sm focus:outline-none focus:ring-green-500 focus:border-green-500 text-white"
                />
                {errors.country && (
                  <p className="mt-1 text-sm text-red-400">{errors.country.message}</p>
                )}
              </div>

              {errorMessage && (
                <p className="mt-4 text-center text-red-400">{errorMessage}</p>
              )}

              <button
                type="submit"
                disabled={loading || cartItems.length === 0}
                className={`w-full py-3 px-4 rounded-md text-lg font-semibold transition-colors ${
                  loading || cartItems.length === 0
                    ? "bg-gray-600 cursor-not-allowed"
                    : "bg-green-600 hover:bg-green-700"
                }`}
              >
                {loading ? "Sipariş Veriliyor..." : "Siparişi Onayla"}
              </button>
            </form>
          </>
        )}
      </div>
    </div>
  );
};

export default Payment;
