import React from "react";
import { Link } from "react-router-dom";
import { Heart, HeartOff, ShoppingCart } from "lucide-react";
import { useFavorites } from "../context/FavoriteContext";
import { useCart } from "../context/CartContext";

const backendUrl = "http://localhost:5000"; // Backend adresini burada tanımladım

const ProductCard = ({ product, showDiscount }) => {
  const { favorites, toggleFavorite } = useFavorites();
  const { addToCart } = useCart();

  const isFavorited = favorites.some((item) => item._id === product._id);
  const isDiscounted =
    showDiscount && product.discountPrice && product.discountPrice < product.price;

  return (
    <div className="bg-gray-800 p-4 rounded shadow hover:shadow-lg relative">
      <Link to={`/product/${product._id}`} className="block">
        <img
          src={`${backendUrl}${product.image}`} // Burada backend adresi ile birleştirildi
          alt={product.name}
          className="mb-4 rounded object-cover h-48 w-full"
        />
        <h3 className="text-xl font-semibold">{product.name}</h3>
        <p className="mt-2 text-gray-300 line-clamp-3">{product.description}</p>
        <div className="mt-4">
          {isDiscounted ? (
            <>
              <span className="text-gray-400 line-through mr-2">{product.price} TL</span>
              <span className="font-bold text-green-400">{product.discountPrice} TL</span>
            </>
          ) : (
            <span className="font-bold text-primary">{product.price} TL</span>
          )}
        </div>
      </Link>

      <button
        onClick={() => toggleFavorite(product)}
        className={`absolute top-3 right-3 p-2 rounded-full ${
          isFavorited ? "bg-red-600 text-white" : "bg-gray-700 text-white"
        } hover:bg-red-700 transition`}
        title={isFavorited ? "Favorilerden çıkar" : "Favorilere ekle"}
        aria-label={isFavorited ? "Favorilerden çıkar" : "Favorilere ekle"}
      >
        {isFavorited ? <Heart size={18} /> : <HeartOff size={18} />}
      </button>

      <button
        onClick={() => addToCart(product)}
        className="mt-4 w-full bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded transition"
        title="Sepete Ekle"
        aria-label="Sepete Ekle"
      >
        <ShoppingCart size={16} className="inline mr-2" />
        Sepete Ekle
      </button>
    </div>
  );
};

export default ProductCard;
