// src/pages/Payment.jsx
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { useCart } from "../context/CartContext";

const Payment = () => {
  const [cardInfo, setCardInfo] = useState({ name: "", number: "", cvv: "" });
  const [error, setError] = useState("");
  const { clearCart } = useCart();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setCardInfo({ ...cardInfo, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const { name, number, cvv } = cardInfo;

    if (name && number.length === 16 && cvv.length === 3) {
      clearCart();
      navigate("/success");
    } else {
      setError("Lütfen tüm kart bilgilerini geçerli şekilde doldurun.");
    }
  };

  return (
    <div className="min-h-screen bg-black text-white flex items-center justify-center p-6">
      <motion.form
        onSubmit={handleSubmit}
        className="bg-gray-800 p-6 rounded-xl shadow-lg w-full max-w-md space-y-4"
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
      >
        <h2 className="text-2xl font-bold text-center">Ödeme Bilgileri</h2>

        {error && <p className="text-red-500 text-sm">{error}</p>}

        <input
          type="text"
          name="name"
          placeholder="Kart Üzerindeki İsim"
          className="w-full p-2 rounded bg-gray-700"
          onChange={handleChange}
        />
        <input
          type="text"
          name="number"
          placeholder="Kart Numarası (16 hane)"
          maxLength={16}
          className="w-full p-2 rounded bg-gray-700"
          onChange={handleChange}
        />
        <input
          type="text"
          name="cvv"
          placeholder="CVV (3 hane)"
          maxLength={3}
          className="w-full p-2 rounded bg-gray-700"
          onChange={handleChange}
        />

        <button
          type="submit"
          className="w-full bg-green-600 hover:bg-green-700 py-2 rounded"
        >
          Ödeme Yap
        </button>
      </motion.form>
    </div>
  );
};

export default Payment;
