// frontend/src/pages/OrderHistory.jsx (GÜNCELLENMİŞ VERSİYON)
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext"; // AuthContext'i dahil et
import { fetchUserOrders } from "../api/orderApi"; // Yeni oluşturduğumuz fetchUserOrders'ı dahil et

const OrderHistory = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null); // Hata state'i
  const navigate = useNavigate();
  const { token, isAuthenticated, loading: authLoading } = useAuth(); // AuthContext'ten token ve loading durumunu al

  useEffect(() => {
    // AuthContext'in yüklenmesini bekle ve kullanıcı oturum açmışsa devam et
    if (!authLoading && !isAuthenticated) {
      navigate("/login"); // Oturum açmamışsa giriş sayfasına yönlendir
      return;
    }
    if (!authLoading && isAuthenticated && token) {
      const getOrders = async () => {
        try {
          setLoading(true);
          setError(null);
          const data = await fetchUserOrders(token); // Token'ı API fonksiyonuna gönder
          setOrders(data);
        } catch (err) {
          console.error("Sipariş geçmişi alınırken hata:", err);
          setError(err.message || "Sipariş geçmişi yüklenirken bir hata oluştu.");
        } finally {
          setLoading(false);
        }
      };
      getOrders();
    }
  }, [authLoading, isAuthenticated, token, navigate]); // Bağımlılıkları güncelledik

  const formatDate = (dateStr) => {
    const date = new Date(dateStr);
    return date.toLocaleDateString("tr-TR", {
      year: "numeric",
      month: "long",
      day: "numeric",
    });
  };

  if (authLoading || loading) { // Hem AuthContext yüklenirken hem de siparişler yüklenirken
    return (
      <div className="min-h-screen bg-gray-900 text-white p-6 flex justify-center items-center">
        <p>Sipariş geçmişi yükleniyor...</p>
      </div>
    );
  }

  if (error) { // Hata durumunda
    return (
      <div className="min-h-screen bg-gray-900 text-white p-6 text-center">
        <h1 className="text-3xl font-bold mb-6">Sipariş Geçmişi</h1>
        <p className="text-red-400">{error}</p>
        <button
          onClick={() => navigate("/login")}
          className="mt-4 inline-block bg-blue-600 hover:bg-blue-700 text-white font-semibold py-2 px-4 rounded-xl"
        >
          Giriş Yap
        </button>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-900 text-white p-6">
      <h1 className="text-3xl font-bold mb-6">Sipariş Geçmişi</h1>

      {orders.length === 0 ? (
        <p>Henüz bir siparişiniz yok.</p>
      ) : (
        <div className="grid gap-6">
          {orders.map((order) => (
            <div
              key={order._id}
              className="bg-gray-800 rounded-xl shadow-md p-4 hover:ring-2 hover:ring-green-400 transition-all"
            >
              <div className="flex justify-between items-center mb-2">
                <div>
                  <p className="text-lg font-semibold">Sipariş No: {order._id}</p>
                  <p className="text-gray-400 text-sm">{formatDate(order.createdAt)}</p>
                </div>
                <div className="text-right">
                  <p className="text-sm text-gray-300">
                    Ürün Sayısı: {order.products?.length || 0} {/* orderItems yerine products kullandık */}
                  </p>
                  <p className="text-sm text-gray-300">
                    Toplam: ₺{order.totalPrice?.toFixed(2)}
                  </p>
                  <p
                    className={`text-sm font-semibold ${
                      order.status === "delivered" ? "text-green-400" : "text-yellow-400" // isDelivered yerine status kontrolü
                    }`}
                  >
                    {/* Backend'deki enum değerlerine göre gösterim */}
                    {order.status === "pending" && "Beklemede"}
                    {order.status === "preparing" && "Hazırlanıyor"}
                    {order.status === "shipped" && "Kargoda"}
                    {order.status === "delivered" && "Teslim Edildi"}
                    {order.status === "cancelled" && "İptal Edildi"}
                  </p>
                </div>
              </div>
              <button
                onClick={() => navigate(`/order/${order._id}`)}
                className="mt-2 inline-block text-sm text-green-400 hover:underline"
              >
                Detayları Görüntüle
              </button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default OrderHistory;
