import React, { createContext, useContext, useEffect, useState, useCallback } from "react";
import { useAuth } from "./AuthContext"; // AuthContext'i içeri aktarıyoruz

export const FavoriteContext = createContext();

export const FavoriteProvider = ({ children }) => {
  const { user, token, isAuthenticated, loading: authLoading } = useAuth(); // AuthContext'ten user, token ve isAuthenticated, loading bilgilerini al
  const [favorites, setFavorites] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // API'nin temel URL'ini çevre değişkeninden alıyoruz
  const API_BASE_URL = process.env.REACT_APP_API_URL || "";

  // Favorileri backend'den çekme fonksiyonu
  const fetchFavorites = useCallback(async () => {
    // Eğer kullanıcı oturum açmamışsa veya auth bilgileri yükleniyorsa, favori çekme
    // token yoksa veya loading durumundaysa istek göndermeyi durdur.
    if (!isAuthenticated || !token || authLoading) {
      setFavorites([]); // Favori listesini temizle
      setLoading(false);
      return;
    }

    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`${API_BASE_URL}/api/favorites`, { // API_BASE_URL eklendi
        headers: {
          "Content-Type": "application/json",
          "Authorization": `Bearer ${token}`, // JWT token'ı Authorization başlığına ekle
        },
      });

      if (!response.ok) {
        if (response.status === 401 || response.status === 403) {
          // Yetkilendirme hatası (token süresi dolmuş vb.)
          console.warn("Favori çekme hatası: Yetkilendirme başarısız veya token geçersiz.");
          // Eğer AuthContext içinde logout fonksiyonu varsa, burada çağırarak kullanıcıyı çıkış yapmaya zorlayabiliriz.
          // authContext.logout(); // Örneğin: const { logout } = useAuth(); ile alıyorsanız
        }
        const errorData = await response.json();
        throw new Error(errorData.message || `Favoriler yüklenirken bir hata oluştu: ${response.status}`);
      }

      const data = await response.json();
      setFavorites(data.map(favItem => ({ // Backend'den gelen favori objesini dönüştür
        ...favItem.product, // Ürün bilgilerini al (backend'in populate ettiğini varsayıyoruz)
        _id: favItem.product._id, // Ürünün _id'si
        favoriteId: favItem._id // Favori kaydının _id'si (silmek için lazım olabilir)
      })));
      console.log("Favoriler backend'den yüklendi:", data);
    } catch (err) {
      console.error("Favori yükleme hatası:", err);
      setError(err.message);
      setFavorites([]); // Hata durumunda favorileri temizle
    } finally {
      setLoading(false);
    }
  }, [isAuthenticated, token, authLoading, API_BASE_URL]); // Bağımlılıkları ekle

  // Kullanıcı durumu veya token değiştiğinde favorileri tekrar çek
  // authLoading durumu da izlenmeli ki, kimlik doğrulama tamamlandıktan sonra istek atılsın.
  useEffect(() => {
    if (!authLoading) { // authLoading bittiğinde favorileri çek
      fetchFavorites();
    }
  }, [fetchFavorites, authLoading]); // user bağımlılığı kaldırıldı, çünkü isAuthenticated ve token zaten yeterli

  // Favori ekle/çıkar fonksiyonu
  const toggleFavorite = useCallback(async (product) => {
    if (!isAuthenticated) {
      console.warn("Favoriye eklemek için giriş yapmalısınız.");
      return;
    }
    if (!token) {
      console.error("Token mevcut değil. Favori işlemi yapılamaz.");
      setError("Yetkilendirme tokenı eksik. Lütfen tekrar giriş yapın.");
      return;
    }

    const isCurrentlyFavorited = favorites.some((item) => item._id === product._id);
    setError(null); // Önceki hataları temizle

    try {
      let response;
      if (isCurrentlyFavorited) {
        // Favorilerden çıkar
        response = await fetch(`${API_BASE_URL}/api/favorites/${product._id}`, { // API_BASE_URL eklendi, Ürünün _id'si ile sil
          method: "DELETE",
          headers: {
            "Authorization": `Bearer ${token}`,
          },
        });
        const data = await response.json();
        if (!response.ok) {
          throw new Error(data.message || `Favoriden çıkarılırken bir hata oluştu: ${response.status}`);
        }
        setFavorites(favorites.filter((item) => item._id !== product._id));
        console.log("Ürün favorilerden çıkarıldı:", product.name);
      } else {
        // Favorilere ekle
        response = await fetch(`${API_BASE_URL}/api/favorites`, { // API_BASE_URL eklendi
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`,
          },
          body: JSON.stringify({ productId: product._id }), // Backend'e sadece ürün ID'si gönder
        });
        const data = await response.json();
        if (!response.ok) {
          throw new Error(data.message || `Favoriye eklenirken bir hata oluştu: ${response.status}`);
        }
        // Backend'den gelen favori objesi içinde populate edilmiş ürün olduğu için
        // doğrudan data.product'ı listeye ekleyebiliriz.
        setFavorites((prevFavorites) => [...prevFavorites, { ...data.product, favoriteId: data._id }]);
        console.log("Ürün favorilere eklendi:", data.product.name);
      }
    } catch (err) {
      console.error("Favori işlemi hatası:", err);
      setError(err.message);
      // Hata durumunda favori listesini güncellemeyebilir veya kullanıcıya bir bildirim gösterebilirsiniz.
    }
  }, [favorites, isAuthenticated, token, API_BASE_URL]); // favorites bağımlılığı eklendi, API_BASE_URL eklendi

  return (
    <FavoriteContext.Provider
      value={{ favorites, loading, error, toggleFavorite, fetchFavorites }} // fetchFavorites de dışarıya açıldı
    >
      {children}
    </FavoriteContext.Provider>
  );
};

export const useFavorites = () => useContext(FavoriteContext);
