import React, { useEffect, useState, useRef } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion";
import { useCart } from "../context/CartContext";
import { useFavorites } from "../context/FavoriteContext";
import { useAuth } from "../context/AuthContext";
import { Heart } from "react-feather";
import { useTheme } from "../context/ThemeContext";

const ProductDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { addToCart } = useCart();
  const { favorites, toggleFavorite } = useFavorites();
  const { theme } = useTheme();
  const { isAuthenticated, user } = useAuth();

  const [product, setProduct] = useState(null);
  const [loadingProduct, setLoadingProduct] = useState(true);
  const [errorProduct, setErrorProduct] = useState(null);

  const [relatedProducts, setRelatedProducts] = useState([]);
  const [loadingRelated, setLoadingRelated] = useState(false);
  const [errorRelated, setErrorRelated] = useState(null);

  const [comments, setComments] = useState([]);
  const [loadingComments, setLoadingComments] = useState(false);
  const [errorComments, setErrorComments] = useState(null);
  const [newComment, setNewComment] = useState("");
  const [submittingComment, setSubmittingComment] = useState(false);

  const [flyingImage, setFlyingImage] = useState(null);
  const productImageRef = useRef(null);
  const cartIconPosition = { x: window.innerWidth - 50, y: 20 };

  // Fetch product details
  useEffect(() => {
    setLoadingProduct(true);
    setErrorProduct(null);

    fetch(`/api/products/${id}`)
      .then((res) => {
        if (!res.ok) {
          if (res.status === 404) throw new Error("Ürün bulunamadı.");
          throw new Error("Ürün yüklenirken bir hata oluştu.");
        }
        return res.json();
      })
      .then((data) => {
        setProduct(data);
        setLoadingProduct(false);
      })
      .catch((err) => {
        console.error("Ürün detay hatası:", err);
        setErrorProduct(err.message);
        setProduct(null);
        setLoadingProduct(false);
      });
  }, [id]);

  // Fetch related products
  useEffect(() => {
    if (product?.category) {
      setLoadingRelated(true);
      setErrorRelated(null);

      fetch(`/api/products?categoryName=${product.category.name}&exclude=${product._id}`)
        .then((res) => {
          if (!res.ok) throw new Error("İlgili ürünler yüklenemedi.");
          return res.json();
        })
        .then((data) => {
          setRelatedProducts(data);
          setLoadingRelated(false);
        })
        .catch((err) => {
          console.error("İlgili ürünler çekilirken hata:", err);
          setErrorRelated("İlgili ürünler yüklenirken bir sorun oluştu.");
          setLoadingRelated(false);
        });
    } else {
      setRelatedProducts([]);
      setLoadingRelated(false);
    }
  }, [product]);

  // Fetch comments
  useEffect(() => {
    if (!product) return;

    setLoadingComments(true);
    setErrorComments(null);

    fetch(`/api/products/${id}/comments`)
      .then((res) => {
        if (!res.ok) throw new Error("Yorumlar yüklenemedi.");
        return res.json();
      })
      .then((data) => {
        setComments(data);
        setLoadingComments(false);
      })
      .catch((err) => {
        console.error("Yorumlar çekilirken hata:", err);
        setErrorComments("Yorumlar yüklenirken bir hata oluştu.");
        setLoadingComments(false);
      });
  }, [id, product]);

  const isFavorited = favorites.some((item) => item._id === product?._id);

  const handleToggleFavorite = () => {
    if (!isAuthenticated) {
      navigate("/login");
    } else {
      toggleFavorite(product);
    }
  };

  // Sepete ekleme animasyonu başlatma
  const handleAddToCartWithAnimation = () => {
    if (!productImageRef.current) {
      addToCart(product);
      return;
    }

    const imgRect = productImageRef.current.getBoundingClientRect();

    setFlyingImage({
      src: product.image || "https://via.placeholder.com/600x400?text=Ürün+Görseli+Mevcut+Değil",
      width: imgRect.width,
      height: imgRect.height,
      x: imgRect.left,
      y: imgRect.top,
    });
  };

  // Animasyon tamamlandığında sepete ekle ve uçan görseli kaldır
  const onAnimationComplete = () => {
    addToCart(product);
    setFlyingImage(null);
  };

  // Yeni yorum gönderme
  const handleSubmitComment = (e) => {
    e.preventDefault();

    if (!isAuthenticated) {
      navigate("/login");
      return;
    }

    if (!newComment.trim()) return;

    setSubmittingComment(true);

    fetch(`/api/products/${id}/comments`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        userId: user._id,
        userName: user.name || user.email,
        text: newComment.trim(),
        date: new Date().toISOString(),
      }),
    })
      .then((res) => {
        if (!res.ok) throw new Error("Yorum gönderilemedi.");
        return res.json();
      })
      .then((savedComment) => {
        setComments((prev) => [savedComment, ...prev]);
        setNewComment("");
        setSubmittingComment(false);
      })
      .catch((err) => {
        console.error("Yorum gönderme hatası:", err);
        alert("Yorum gönderilirken bir hata oluştu.");
        setSubmittingComment(false);
      });
  };

  // Tema ve stil sınıfları
  const containerBgClass = theme === "light" ? "bg-white" : "bg-gray-900";
  const textClass = theme === "light" ? "text-gray-900" : "text-white";
  const cardBgClass = theme === "light" ? "bg-gray-50" : "bg-gray-800";
  const cardTextClass = theme === "light" ? "text-gray-800" : "text-gray-100";
  const buttonClass = "bg-primary hover:bg-primary-dark text-white px-6 py-2 rounded transition";
  const favoriteButtonClass = isFavorited ? "bg-danger hover:bg-red-700" : "bg-muted hover:bg-gray-600";
  const relatedCardHoverClass = "hover:shadow-custom-medium transition-shadow duration-300";

  if (loadingProduct)
    return <p className={`text-center mt-10 ${textClass}`}>Ürün yükleniyor...</p>;

  if (errorProduct)
    return <p className={`text-center mt-10 text-danger`}>{errorProduct}</p>;

  if (!product)
    return <p className={`text-center mt-10 ${textClass}`}>Ürün bulunamadı.</p>;

  return (
    <div className={`container mx-auto px-4 py-8 min-h-screen ${containerBgClass} ${textClass}`}>
      {/* Ürün Detay Bölümü */}
      <div className={`flex flex-col md:flex-row gap-8 ${cardBgClass} p-6 rounded-lg shadow-custom-medium`}>
        <div className="md:w-1/2">
          <img
            ref={productImageRef}
            src={product.image || "https://via.placeholder.com/600x400?text=Ürün+Görseli+Mevcut+Değil"}
            alt={product.name}
            className="w-full h-auto object-cover rounded-lg shadow-custom-light"
          />
        </div>
        <div className="md:w-1/2 flex flex-col justify-between">
          <div>
            <h1 className="text-4xl font-bold mb-4">{product.name}</h1>
            {product.category && (
              <p className="text-primary text-lg mb-2">
                Kategori:{" "}
                <Link to={`/categories?name=${product.category.name}`} className="underline hover:no-underline">
                  {product.category.name}
                </Link>
              </p>
            )}
            <p className={`text-lg mb-4 ${cardTextClass}`}>{product.description}</p>

            {product.discountPrice ? (
              <div className="mb-4">
                <p className="line-through text-red-500 text-lg">{product.price} TL</p>
                <p className="text-green-500 text-3xl font-bold">{product.discountPrice} TL</p>
              </div>
            ) : (
              <p className="text-3xl font-bold mb-4 text-primary">{product.price} TL</p>
            )}
          </div>

          <div className="flex flex-col sm:flex-row gap-4 mt-6">
            <motion.button
              onClick={handleAddToCartWithAnimation}
              className={`${buttonClass}`}
              whileTap={{ scale: 0.95 }}
            >
              Sepete Ekle
            </motion.button>

            <button
              onClick={handleToggleFavorite}
              className={`flex items-center justify-center gap-2 px-6 py-2 rounded transition ${favoriteButtonClass} text-white`}
            >
              {isFavorited ? (
                <>
                  <Heart className="mr-1" size={20} fill="white" /> Favorilerden Çıkar
                </>
              ) : (
                <>
                  <Heart className="mr-1" size={20} fill="none" /> Favorilere Ekle
                </>
              )}
            </button>
          </div>
        </div>
      </div>

      {/* Yorumlar Bölümü */}
      <div className={`mt-12 ${cardBgClass} p-6 rounded-lg shadow-custom-medium`}>
        <h2 className="text-3xl font-bold mb-6 text-center">Yorumlar</h2>

        {loadingComments ? (
          <p className="text-center">Yorumlar yükleniyor...</p>
        ) : errorComments ? (
          <p className="text-danger text-center">{errorComments}</p>
        ) : comments.length === 0 ? (
          <p className="text-center text-muted">Henüz yorum yapılmamış.</p>
        ) : (
          <ul className="space-y-4 max-h-72 overflow-y-auto">
            {comments.map((comment) => (
              <li key={comment._id} className="border-b border-gray-300 pb-2">
                <p className="font-semibold">{comment.userName}</p>
                <p className="text-sm text-muted">{new Date(comment.date).toLocaleString()}</p>
                <p className="mt-1">{comment.text}</p>
              </li>
            ))}
          </ul>
        )}

        {/* Yorum gönderme formu */}
        {isAuthenticated ? (
          <form onSubmit={handleSubmitComment} className="mt-6 flex flex-col gap-4">
            <textarea
              value={newComment}
              onChange={(e) => setNewComment(e.target.value)}
              placeholder="Yorumunuzu yazın..."
              rows={3}
              className="p-3 rounded border border-gray-400 resize-none focus:outline-none focus:ring focus:ring-primary"
              disabled={submittingComment}
              required
            />
            <button
              type="submit"
              disabled={submittingComment}
              className={`${buttonClass} self-end`}
            >
              {submittingComment ? "Gönderiliyor..." : "Gönder"}
            </button>
          </form>
        ) : (
          <p className="mt-6 text-center">
            Yorum yapmak için <Link to="/login" className="text-primary underline">giriş yapmalısınız.</Link>
          </p>
        )}
      </div>

      {/* İlgili Ürünler Bölümü */}
      <div className="mt-12">
        <h2 className="text-3xl font-bold mb-6">İlgili Ürünler</h2>

        {loadingRelated ? (
          <p>Yükleniyor...</p>
        ) : errorRelated ? (
          <p className="text-danger">{errorRelated}</p>
        ) : relatedProducts.length === 0 ? (
          <p>İlgili ürün bulunamadı.</p>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {relatedProducts.map((item) => (
              <Link
                key={item._id}
                to={`/products/${item._id}`}
                className={`block rounded-lg overflow-hidden shadow-custom-light ${cardBgClass} ${relatedCardHoverClass}`}
              >
                <img
                  src={item.image || "https://via.placeholder.com/600x400?text=Ürün+Görseli+Mevcut+Değil"}
                  alt={item.name}
                  className="w-full h-48 object-cover"
                  loading="lazy"
                />
                <div className="p-4">
                  <h3 className={`font-semibold mb-2 ${cardTextClass}`}>{item.name}</h3>
                  <p className={`text-lg font-bold ${cardTextClass}`}>
                    {item.discountPrice ? (
                      <>
                        <span className="line-through text-red-500 mr-2">{item.price} TL</span>
                        <span className="text-green-600">{item.discountPrice} TL</span>
                      </>
                    ) : (
                      <span>{item.price} TL</span>
                    )}
                  </p>
                </div>
              </Link>
            ))}
          </div>
        )}
      </div>

      {/* Sepete uçan görsel animasyonu */}
      <AnimatePresence>
        {flyingImage && (
          <motion.img
            src={flyingImage.src}
            initial={{ opacity: 1, width: flyingImage.width, height: flyingImage.height, x: flyingImage.x, y: flyingImage.y, position: "fixed", top: 0, left: 0, zIndex: 9999, borderRadius: 10 }}
            animate={{ x: cartIconPosition.x, y: cartIconPosition.y, width: 30, height: 30, opacity: 0 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 1, ease: "easeInOut" }}
            onAnimationComplete={onAnimationComplete}
          />
        )}
      </AnimatePresence>
    </div>
  );
};

export default ProductDetail;
