import React, { useEffect, useState, useRef } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion";
import { useCart } from "../context/CartContext";
import { useFavorites } from "../context/FavoriteContext";
import { useAuth } from "../context/AuthContext";
import { Heart } from "react-feather";
import { useTheme } from "../context/ThemeContext";

const ProductDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { addToCart } = useCart();
  const { favorites, toggleFavorite } = useFavorites();
  const { isAuthenticated, user } = useAuth();
  const { theme } = useTheme();

  const [product, setProduct] = useState(null);
  const [loadingProduct, setLoadingProduct] = useState(true);
  const [errorProduct, setErrorProduct] = useState(null);

  const [relatedProducts, setRelatedProducts] = useState([]);
  const [loadingRelated, setLoadingRelated] = useState(false);
  const [errorRelated, setErrorRelated] = useState(null);

  const [flyingImage, setFlyingImage] = useState(null);
  const productImageRef = useRef(null);
  const cartIconPosition = { x: window.innerWidth - 50, y: 20 };

  // Yorumlar için state
  const [comments, setComments] = useState([]);
  const [commentInput, setCommentInput] = useState("");
  const [commentError, setCommentError] = useState("");
  const [commentSuccess, setCommentSuccess] = useState("");

  useEffect(() => {
    setLoadingProduct(true);
    setErrorProduct(null);

    fetch(`/api/products/${id}`)
      .then((res) => {
        if (!res.ok) {
          if (res.status === 404) throw new Error("Ürün bulunamadı.");
          throw new Error("Ürün yüklenirken bir hata oluştu.");
        }
        return res.json();
      })
      .then((data) => {
        setProduct(data);
        setLoadingProduct(false);
      })
      .catch((err) => {
        setErrorProduct(err.message);
        setLoadingProduct(false);
      });
  }, [id]);

  useEffect(() => {
    if (product && product.category) {
      setLoadingRelated(true);
      fetch(`/api/products?categoryName=${product.category.name}&exclude=${product._id}`)
        .then((res) => {
          if (!res.ok) throw new Error("İlgili ürünler yüklenemedi.");
          return res.json();
        })
        .then((data) => {
          setRelatedProducts(data);
          setLoadingRelated(false);
        })
        .catch((err) => {
          setErrorRelated("İlgili ürünler yüklenirken bir hata oluştu.");
          setLoadingRelated(false);
        });
    }
  }, [product]);

  // Yorumları API'den çek
  useEffect(() => {
    fetch(`/api/products/${id}/comments`)
      .then((res) => res.json())
      .then((data) => setComments(data))
      .catch((err) => console.error("Yorumlar alınamadı", err));
  }, [id]);

  // Yorum gönderme işlemi
  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    setCommentError("");
    setCommentSuccess("");

    if (!commentInput.trim()) {
      setCommentError("Yorum boş bırakılamaz.");
      return;
    }

    try {
      const response = await fetch(`/api/products/${id}/comments`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify({ text: commentInput }),
      });

      if (!response.ok) throw new Error("Yorum gönderilemedi.");

      const newComment = await response.json();
      setComments((prev) => [newComment, ...prev]);
      setCommentInput("");
      setCommentSuccess("Yorumunuz başarıyla eklendi.");
    } catch (error) {
      setCommentError("Yorum eklenirken hata oluştu.");
    }
  };

  if (loadingProduct) return <div>Yükleniyor...</div>;
  if (errorProduct) return <div>Hata: {errorProduct}</div>;
  if (!product) return <div>Ürün bulunamadı.</div>;

  return (
    <div className={`product-detail ${theme}`}>
      <div className="product-info">
        <img
          src={product.image}
          alt={product.name}
          ref={productImageRef}
          style={{ width: "300px", height: "300px" }}
        />
        <h2>{product.name}</h2>
        <p>{product.description}</p>
        <p>Fiyat: {product.price}₺</p>
        <button onClick={() => addToCart(product)}>Sepete Ekle</button>
        {isAuthenticated && (
          <button onClick={() => toggleFavorite(product)}>
            <Heart color={favorites.some((fav) => fav._id === product._id) ? "red" : "gray"} />
          </button>
        )}
      </div>

      <div className="comment-section">
        <h3>Yorumlar</h3>

        {/* Yorum Formu */}
        {isAuthenticated ? (
          <form onSubmit={handleCommentSubmit}>
            <textarea
              value={commentInput}
              onChange={(e) => setCommentInput(e.target.value)}
              placeholder="Yorumunuzu yazın..."
              rows={3}
              style={{ width: "100%" }}
            />
            <button type="submit">Yorumu Gönder</button>
            {commentError && <p style={{ color: "red" }}>{commentError}</p>}
            {commentSuccess && <p style={{ color: "green" }}>{commentSuccess}</p>}
          </form>
        ) : (
          <p>Yorum yapabilmek için giriş yapmalısınız.</p>
        )}

        {/* Yorumları Listele */}
        {comments.length === 0 ? (
          <p>Henüz yorum yapılmamış.</p>
        ) : (
          <ul>
            {comments.map((comment) => (
              <li key={comment._id}>
                <strong>{comment.username || "Kullanıcı"}:</strong> {comment.text}
              </li>
            ))}
          </ul>
        )}
      </div>

      <div className="related-products">
        <h3>Benzer Ürünler</h3>
        {loadingRelated && <p>Yükleniyor...</p>}
        {errorRelated && <p>{errorRelated}</p>}
        {!loadingRelated &&
          relatedProducts.map((p) => (
            <Link key={p._id} to={`/products/${p._id}`}>
              <div>{p.name}</div>
            </Link>
          ))}
      </div>
    </div>
  );
};

export default ProductDetail;
