// src/context/AuthContext.jsx
import React, { createContext, useContext, useEffect, useState } from "react";
import { loginUser as apiLoginUser, registerUser as apiRegisterUser } from "../api/authApi";

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [authToken, setAuthToken] = useState(null);

  useEffect(() => {
    console.log("AuthContext useEffect çalıştı.");
    const tokenFromStorage = localStorage.getItem("token");
    const userData = localStorage.getItem("user");

    console.log("localStorage token:", tokenFromStorage);
    console.log("localStorage user data (string):", userData);

    if (tokenFromStorage && userData) {
      try {
        const parsedUser = JSON.parse(userData);
        setIsAuthenticated(true);
        setAuthToken(tokenFromStorage);
        setUser(parsedUser);
        console.log("AuthContext: Kullanıcı yüklendi:", parsedUser);
      } catch (e) {
        console.error("AuthContext: Error parsing user data from localStorage:", e);
        logout(); // Hatalı veri varsa çıkış yap
        console.log("AuthContext: localStorage verisi parse edilemedi, çıkış yapıldı.");
      }
    } else {
      setIsAuthenticated(false);
      setUser(null);
      setAuthToken(null);
      console.log("AuthContext: localStorage'da token veya user bulunamadı.");
    }

    setLoading(false);
    console.log("AuthContext: Loading state false olarak ayarlandı.");
  }, []);

  const login = async (email, password) => {
    try {
      const data = await apiLoginUser(email, password);
      localStorage.setItem("token", data.token);
      localStorage.setItem("user", JSON.stringify(data.user)); // Stringify edildiğinden emin ol

      setIsAuthenticated(true);
      setUser(data.user);
      setAuthToken(data.token);
      console.log("AuthContext: Giriş Başarılı, user:", data.user, "token:", data.token);
      return true;
    } catch (error) {
      console.error("AuthContext: Login error:", error.message);
      return false;
    }
  };

  const register = async (username, email, password, confirmPassword) => {
    try {
      const data = await apiRegisterUser(username, email, password, confirmPassword);
      console.log("AuthContext: Kayıt Başarılı:", data);
      return true;
    } catch (error) {
      console.error("AuthContext: Register error:", error.message);
      throw error;
    }
  };

  const updateProfile = (updatedUserData) => {
    localStorage.setItem("user", JSON.stringify(updatedUserData)); // Güncel veriyi de stringify et
    setUser(updatedUserData);
    console.log("AuthContext: Profil güncellendi:", updatedUserData);
  };

  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    setIsAuthenticated(false);
    setUser(null);
    setAuthToken(null);
    console.log("AuthContext: Çıkış yapıldı.");
  };

  return (
    <AuthContext.Provider value={{ isAuthenticated, user, loading, token: authToken, login, logout, register, setUser: updateProfile }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
