import React, { useEffect, useState } from "react";
import axios from "axios";

const AdminProductList = ({
  categories = [],   // default boş dizi
  products = [],     // default boş dizi
  setProducts,
}) => {
  const [filterCategory, setFilterCategory] = useState("");
  const [filterMinPrice, setFilterMinPrice] = useState("");
  const [filterMaxPrice, setFilterMaxPrice] = useState("");
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const productsPerPage = 10;

  // Düzenleme modal state'leri
  const [editingProduct, setEditingProduct] = useState(null);
  const [editName, setEditName] = useState("");
  const [editPrice, setEditPrice] = useState("");
  const [editCategory, setEditCategory] = useState("");

  useEffect(() => {
    if (!Array.isArray(products)) {
      setFilteredProducts([]);
      return;
    }

    let temp = [...products];

    if (filterCategory) {
      temp = temp.filter((p) => p.category === filterCategory);
    }
    if (filterMinPrice !== "") {
      temp = temp.filter((p) => p.price >= Number(filterMinPrice));
    }
    if (filterMaxPrice !== "") {
      temp = temp.filter((p) => p.price <= Number(filterMaxPrice));
    }

    setFilteredProducts(temp);
    setCurrentPage(1);
  }, [products, filterCategory, filterMinPrice, filterMaxPrice]);

  const indexOfLastProduct = currentPage * productsPerPage;
  const indexOfFirstProduct = indexOfLastProduct - productsPerPage;
  const currentProducts = filteredProducts.slice(
    indexOfFirstProduct,
    indexOfLastProduct
  );
  const totalPages = Math.ceil(filteredProducts.length / productsPerPage);

  const handleDelete = async (id) => {
    if (window.confirm("Ürünü silmek istediğinize emin misiniz?")) {
      try {
        await axios.delete(`/api/products/${id}`);
        const updatedProducts = products.filter((p) => p._id !== id);
        setProducts(updatedProducts);
      } catch (error) {
        alert("Ürün silinirken hata oluştu.");
        console.error(error);
      }
    }
  };

  const openEditModal = (product) => {
    setEditingProduct(product);
    setEditName(product.name || "");
    setEditPrice(product.price || "");
    setEditCategory(product.category || "");
  };

  const saveEdit = async () => {
    if (!editName.trim()) {
      alert("Ürün adı boş olamaz.");
      return;
    }
    if (isNaN(editPrice) || editPrice === "") {
      alert("Geçerli bir fiyat giriniz.");
      return;
    }
    if (!editCategory) {
      alert("Kategori seçiniz.");
      return;
    }

    const updatedProduct = {
      ...editingProduct,
      name: editName,
      price: Number(editPrice),
      category: editCategory,
    };

    try {
      const { data } = await axios.put(
        `/api/products/${updatedProduct._id}`,
        updatedProduct
      );

      const updatedProducts = products.map((p) =>
        p._id === data._id ? data : p
      );
      setProducts(updatedProducts);
      setEditingProduct(null);
    } catch (error) {
      alert("Ürün güncellenirken hata oluştu.");
      console.error(error);
    }
  };

  const cancelEdit = () => {
    setEditingProduct(null);
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4 text-white">Ürün Yönetimi</h2>

      <div className="mb-4 flex gap-4 flex-wrap">
        <select
          className="bg-gray-800 text-white p-2 rounded"
          value={filterCategory}
          onChange={(e) => setFilterCategory(e.target.value)}
        >
          <option value="">Tüm Kategoriler</option>
          {categories.length > 0 &&
            categories.map((cat) => (
              <option key={cat._id} value={cat._id}>
                {cat.name}
              </option>
            ))}
        </select>

        <input
          type="number"
          placeholder="Min Fiyat"
          className="bg-gray-800 text-white p-2 rounded w-24"
          value={filterMinPrice}
          onChange={(e) => setFilterMinPrice(e.target.value)}
          min="0"
        />
        <input
          type="number"
          placeholder="Max Fiyat"
          className="bg-gray-800 text-white p-2 rounded w-24"
          value={filterMaxPrice}
          onChange={(e) => setFilterMaxPrice(e.target.value)}
          min="0"
        />
      </div>

      <table className="w-full text-left border-collapse border border-gray-700 text-white">
        <thead>
          <tr>
            <th className="border border-gray-600 p-2">ID</th>
            <th className="border border-gray-600 p-2">Görsel</th>
            <th className="border border-gray-600 p-2">Ürün Adı</th>
            <th className="border border-gray-600 p-2">Kategori</th>
            <th className="border border-gray-600 p-2">Fiyat</th>
            <th className="border border-gray-600 p-2">İşlemler</th>
          </tr>
        </thead>
        <tbody>
          {currentProducts.length === 0 ? (
            <tr>
              <td colSpan="6" className="text-center p-4">
                Ürün bulunamadı.
              </td>
            </tr>
          ) : (
            currentProducts.map((product) => (
              <tr key={product._id} className="hover:bg-gray-700">
                <td className="border border-gray-600 p-2">{product._id}</td>
                <td className="border border-gray-600 p-2">
                  {product.image ? (
                    <img
                      src={product.image}
                      alt={product.name}
                      className="w-12 h-12 object-cover rounded"
                    />
                  ) : (
                    <span>Yok</span>
                  )}
                </td>
                <td className="border border-gray-600 p-2">{product.name}</td>
                <td className="border border-gray-600 p-2">
                  {categories.find((cat) => cat._id === product.category)
                    ?.name || "Bilinmiyor"}
                </td>
                <td className="border border-gray-600 p-2">{product.price} ₺</td>
                <td className="border border-gray-600 p-2 flex gap-2">
                  <button
                    onClick={() => openEditModal(product)}
                    className="bg-blue-600 hover:bg-blue-700 text-white px-3 py-1 rounded"
                  >
                    Düzenle
                  </button>
                  <button
                    onClick={() => handleDelete(product._id)}
                    className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded"
                  >
                    Sil
                  </button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>

      {/* Sayfalama */}
      <div className="mt-4 flex justify-center gap-2 text-white">
        <button
          disabled={currentPage === 1}
          onClick={() => setCurrentPage((prev) => prev - 1)}
          className={`px-3 py-1 rounded ${
            currentPage === 1
              ? "bg-gray-600 cursor-not-allowed"
              : "bg-gray-800 hover:bg-gray-700"
          }`}
        >
          Önceki
        </button>
        {Array.from({ length: totalPages }, (_, i) => (
          <button
            key={i + 1}
            onClick={() => setCurrentPage(i + 1)}
            className={`px-3 py-1 rounded ${
              currentPage === i + 1
                ? "bg-blue-600"
                : "bg-gray-800 hover:bg-gray-700"
            }`}
          >
            {i + 1}
          </button>
        ))}
        <button
          disabled={currentPage === totalPages || totalPages === 0}
          onClick={() => setCurrentPage((prev) => prev + 1)}
          className={`px-3 py-1 rounded ${
            currentPage === totalPages || totalPages === 0
              ? "bg-gray-600 cursor-not-allowed"
              : "bg-gray-800 hover:bg-gray-700"
          }`}
        >
          Sonraki
        </button>
      </div>

      {/* Düzenleme Modal */}
      {editingProduct && (
        <div className="fixed inset-0 bg-black bg-opacity-70 flex justify-center items-center z-50">
          <div className="bg-gray-900 p-6 rounded w-96 text-white">
            <h3 className="text-xl font-semibold mb-4">Ürünü Düzenle</h3>
            <label className="block mb-2">
              Ürün Adı:
              <input
                type="text"
                value={editName}
                onChange={(e) => setEditName(e.target.value)}
                className="w-full p-2 rounded bg-gray-800 text-white mt-1"
              />
            </label>
            <label className="block mb-2">
              Fiyat:
              <input
                type="number"
                value={editPrice}
                onChange={(e) => setEditPrice(e.target.value)}
                className="w-full p-2 rounded bg-gray-800 text-white mt-1"
                min="0"
              />
            </label>
            <label className="block mb-4">
              Kategori:
              <select
                value={editCategory}
                onChange={(e) => setEditCategory(e.target.value)}
                className="w-full p-2 rounded bg-gray-800 text-white mt-1"
              >
                <option value="">Kategori Seçiniz</option>
                {categories.length > 0 &&
                  categories.map((cat) => (
                    <option key={cat._id} value={cat._id}>
                      {cat.name}
                    </option>
                  ))}
              </select>
            </label>

            <div className="flex justify-end gap-3">
              <button
                onClick={cancelEdit}
                className="px-4 py-2 bg-gray-700 hover:bg-gray-600 rounded"
              >
                İptal
              </button>
              <button
                onClick={saveEdit}
                className="px-4 py-2 bg-blue-600 hover:bg-blue-700 rounded"
              >
                Kaydet
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminProductList;
