import React, { useRef, useState, useEffect } from 'react'; // useEffect eklendi
import { Link } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';
import { useTheme } from '../context/ThemeContext';
import { useCart } from '../context/CartContext';
import { ShoppingCart, Heart } from 'react-feather';
import { useFavorites } from '../context/FavoriteContext';

const ProductCard = ({ product }) => {
    const { theme } = useTheme();
    const { addToCart } = useCart();
    const { favorites, toggleFavorite } = useFavorites();

    // --- ANİMASYON İÇİN GEREKLİ STATE VE REF'LER ---
    const [flyingImage, setFlyingImage] = useState(null);
    const productImageRef = useRef(null);

    // Sepet ikonunun konumu: Sağ üstte, padding ve ikon boyutuna göre ayarlandı.
    // Navbar'daki sepet ikonunun konumuna göre dinamik olarak hesaplandı.
    const [cartIconPosition, setCartIconPosition] = useState({ x: 0, y: 0 });

    useEffect(() => {
        const updateCartIconPosition = () => {
            const rightOffset = 24 + 16 + 20; // Sepet ikonu boyutu + sağdan boşluklar + tahmini padding
            const topOffset = 30; // Navbar'ın yüksekliğinin ortası veya ikona göre ayarlandı.

            setCartIconPosition({
                x: window.innerWidth - rightOffset,
                y: topOffset,
            });
        };

        updateCartIconPosition();
        window.addEventListener("resize", updateCartIconPosition);

        return () => {
            window.removeEventListener("resize", updateCartIconPosition);
        };
    }, []);
    // ------------------------------------------

    const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || "";
    const IMAGE_ROOT_URL = "http://localhost:5000/uploads";

    const getProductImageUrl = (imageName) => {
        if (!imageName) {
            return "https://via.placeholder.com/400x300?text=Ürün+Görseli+Mevcut+Değil";
        }
        if (imageName.startsWith('uploads/') || imageName.startsWith('/uploads/')) {
            const serverRootUrl = API_BASE_URL.replace('/api', '');
            const normalizedImagePath = imageName.startsWith('/') ? imageName : `/${imageName}`;
            return `${serverRootUrl}${normalizedImagePath}`;
        }
        return `${IMAGE_ROOT_URL}/${imageName}`;
    };

    const cardBgClass = theme === 'light' ? 'bg-white' : 'bg-gray-800';
    const textClass = theme === 'light' ? 'text-gray-900' : 'text-white';
    const priceClass = theme === 'light' ? 'text-primary' : 'text-primary-light';
    const discountPriceClass = theme === 'light' ? 'text-green-600' : 'text-green-400';
    const oldPriceClass = theme === 'light' ? 'text-gray-500 line-through' : 'text-gray-400 line-through';
    const buttonClass = "bg-primary hover:bg-primary-dark text-white p-2 rounded-full transition duration-300 transform hover:scale-110";
    const favoriteButtonClass = (isFavorited) =>
        isFavorited
            ? "text-danger hover:text-red-700"
            : "text-gray-400 hover:text-red-500";

    const isFavorited = favorites.some(item => item._id === product._id);

    const handleAddToCartWithAnimation = (e) => {
        e.stopPropagation();

        if (!productImageRef.current) {
            addToCart(product);
            return;
        }

        const imgRect = productImageRef.current.getBoundingClientRect();

        setFlyingImage({
            src: getProductImageUrl(product.image),
            width: imgRect.width,
            height: imgRect.height,
            x: imgRect.left,
            y: imgRect.top,
        });
    };

    const onAnimationComplete = () => {
        addToCart(product);
        setFlyingImage(null);
    };

    return (
        <motion.div
            className={`relative border border-gray-200 rounded-lg p-4 shadow-sm flex flex-col items-center justify-between ${cardBgClass} ${textClass}`}
            whileHover={{ y: -5, boxShadow: theme === 'light' ? '0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05)' : '0 10px 15px -3px rgba(255, 255, 255, 0.1), 0 4px 6px -2px rgba(255, 255, 255, 0.05)' }}
            transition={{ duration: 0.2 }}
        >
            <button
                onClick={(e) => {
                    e.stopPropagation();
                    toggleFavorite(product);
                }}
                className={`absolute top-3 right-3 ${favoriteButtonClass(isFavorited)} z-10`}
                aria-label="Favorilere ekle/kaldı"
            >
                <Heart size={24} fill={isFavorited ? "currentColor" : "none"} />
            </button>
            <Link to={`/products/${product._id}`} className="flex flex-col items-center text-center w-full">
                <img
                    ref={productImageRef}
                    src={getProductImageUrl(product.image)}
                    alt={product.name}
                    className="w-full h-48 object-contain mb-4 rounded"
                />
                <h3 className="text-lg font-semibold mb-2 line-clamp-2">{product.name}</h3>
                {product.discountPrice ? (
                    <div className="flex flex-col items-center">
                        <span className={`${oldPriceClass} text-sm`}>{product.price} TL</span>
                        <span className={`${discountPriceClass} text-xl font-bold`}>{product.discountPrice} TL</span>
                    </div>
                ) : (
                    <span className={`${priceClass} text-xl font-bold`}>{product.price} TL</span>
                )}
            </Link>
            <button
                onClick={handleAddToCartWithAnimation}
                className={`mt-4 ${buttonClass}`}
                aria-label="Sepete ekle"
            >
                <ShoppingCart size={20} />
            </button>

            {/* --- UÇAN RESİM ANİMASYONU --- */}
            <AnimatePresence>
                {flyingImage && (
                    <motion.img
                        src={flyingImage.src}
                        initial={{
                            width: flyingImage.width,
                            height: flyingImage.height,
                            x: flyingImage.x,
                            y: flyingImage.y,
                            position: "fixed",
                            zIndex: 9999,
                        }}
                        animate={{
                            x: cartIconPosition.x,
                            y: cartIconPosition.y,
                            width: 30,
                            height: 30,
                            opacity: 0.5,
                        }}
                        transition={{ duration: 0.8, ease: "easeInOut" }}
                        exit={{ opacity: 0 }}
                        onAnimationComplete={onAnimationComplete}
                    />
                )}
            </AnimatePresence>
            {/* ----------------------------- */}
        </motion.div>
    );
};

export default ProductCard;
