import React, { useEffect, useState, useRef } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion"; // Animasyonlar için
import { useCart } from "../context/CartContext"; // Sepet işlemleri
import { useFavorites } from "../context/FavoriteContext"; // Favoriler işlemleri
import { useAuth } from "../context/AuthContext"; // Kullanıcı oturum kontrolü
import { Heart } from "react-feather"; // Kalp ikonu
import { useTheme } from "../context/ThemeContext"; // Tema yönetimi

const ProductDetail = () => {
  const { id } = useParams(); // URL'den ürün ID'sini al
  const navigate = useNavigate();

  const { addToCart } = useCart();
  const { favorites, toggleFavorite } = useFavorites();
  const { isAuthenticated } = useAuth();
  const { theme } = useTheme();

  // Ürün ve ilgili ürün durumları
  const [product, setProduct] = useState(null);
  const [loadingProduct, setLoadingProduct] = useState(true);
  const [errorProduct, setErrorProduct] = useState(null);

  const [relatedProducts, setRelatedProducts] = useState([]);
  const [loadingRelated, setLoadingRelated] = useState(false);
  const [errorRelated, setErrorRelated] = useState(null);

  // Sepete ekle animasyonu için görsel ve konum bilgisi
  const [flyingImage, setFlyingImage] = useState(null);
  const productImageRef = useRef(null);
  const cartIconPosition = { x: window.innerWidth - 50, y: 20 };

  // Ürün detaylarını sunucudan çek
  useEffect(() => {
    setLoadingProduct(true);
    setErrorProduct(null);

    fetch(`/api/products/${id}`)
      .then((res) => {
        if (!res.ok) {
          if (res.status === 404) throw new Error("Ürün bulunamadı.");
          throw new Error("Ürün yüklenirken bir hata oluştu.");
        }
        return res.json();
      })
      .then((data) => {
        setProduct(data);
        setLoadingProduct(false);
      })
      .catch((err) => {
        console.error("Ürün detay hatası:", err);
        setErrorProduct(err.message);
        setLoadingProduct(false);
      });
  }, [id]);

  // İlgili ürünleri kategoriye göre getir
  useEffect(() => {
    if (product && product.category) {
      setLoadingRelated(true);
      fetch(`/api/products?categoryName=${product.category.name}&exclude=${product._id}`)
        .then((res) => {
          if (!res.ok) throw new Error("İlgili ürünler yüklenemedi.");
          return res.json();
        })
        .then((data) => {
          setRelatedProducts(data);
          setLoadingRelated(false);
        })
        .catch((err) => {
          console.error("İlgili ürünler hatası:", err);
          setErrorRelated("İlgili ürünler yüklenirken bir sorun oluştu.");
          setLoadingRelated(false);
        });
    } else if (product && !product.category) {
      setRelatedProducts([]);
    }
  }, [product]);

  // Ürün favorilerde mi kontrolü
  const isFavorited = favorites.some((item) => item._id === product?._id);

  // Favori ekle/çıkar işlemi
  const handleToggleFavorite = () => {
    if (!isAuthenticated) navigate("/login");
    else toggleFavorite(product);
  };

  // Sepete ekle ve animasyonu başlat
  const handleAddToCartWithAnimation = () => {
    if (!productImageRef.current) {
      addToCart(product);
      return;
    }

    const imgRect = productImageRef.current.getBoundingClientRect();
    setFlyingImage({
      src: product.image || "https://via.placeholder.com/600x400?text=Ürün+Görseli+Mevcut+Değil",
      width: imgRect.width,
      height: imgRect.height,
      x: imgRect.left,
      y: imgRect.top,
    });
  };

  // Animasyon bittiğinde sepete ekle
  const onAnimationComplete = () => {
    addToCart(product);
    setFlyingImage(null);
  };

  // Tema sınıfları
  const containerBgClass = theme === "light" ? "bg-white" : "bg-gray-900";
  const textClass = theme === "light" ? "text-gray-900" : "text-white";
  const cardBgClass = theme === "light" ? "bg-gray-50" : "bg-gray-800";
  const cardTextClass = theme === "light" ? "text-gray-800" : "text-gray-100";
  const buttonClass = "bg-primary hover:bg-primary-dark text-white px-6 py-2 rounded transition";
  const favoriteButtonClass = isFavorited ? "bg-danger hover:bg-red-700" : "bg-muted hover:bg-gray-600";
  const relatedCardHoverClass = "hover:shadow-custom-medium transition-shadow duration-300";

  // Yükleme ve hata durumları
  if (loadingProduct) return <p className={`text-center mt-10 ${textClass}`}>Ürün yükleniyor...</p>;
  if (errorProduct) return <p className="text-center mt-10 text-danger">{errorProduct}</p>;
  if (!product) return <p className={`text-center mt-10 ${textClass}`}>Ürün bulunamadı.</p>;

  return (
    <div className={`container mx-auto px-4 py-8 min-h-screen ${containerBgClass} ${textClass}`}>
      {/* Ürün Detay Kartı */}
      <div className={`flex flex-col md:flex-row gap-8 ${cardBgClass} p-6 rounded-lg shadow-custom-medium`}>
        <div className="md:w-1/2">
          <img
            ref={productImageRef}
            src={product.image || "https://via.placeholder.com/600x400?text=Ürün+Görseli+Mevcut+Değil"}
            alt={product.name}
            className="w-full h-auto object-cover rounded-lg shadow-custom-light"
          />
        </div>
        <div className="md:w-1/2 flex flex-col justify-between">
          <div>
            <h1 className="text-4xl font-bold mb-4">{product.name}</h1>
            {product.category && (
              <p className="text-primary text-lg mb-2">
                Kategori:{" "}
                <Link to={`/categories?name=${product.category.name}`} className="underline hover:no-underline">
                  {product.category.name}
                </Link>
              </p>
            )}
            <p className={`text-lg mb-4 ${cardTextClass}`}>{product.description}</p>
            {product.discountPrice ? (
              <div className="mb-4">
                <p className="line-through text-red-500 text-lg">{product.price} TL</p>
                <p className="text-green-500 text-3xl font-bold">{product.discountPrice} TL</p>
              </div>
            ) : (
              <p className="text-3xl font-bold mb-4 text-primary">{product.price} TL</p>
            )}
          </div>

          <div className="flex flex-col sm:flex-row gap-4 mt-6">
            <motion.button
              onClick={handleAddToCartWithAnimation}
              className={buttonClass}
              whileTap={{ scale: 0.95 }}
            >
              Sepete Ekle
            </motion.button>

            <button
              onClick={handleToggleFavorite}
              className={`flex items-center justify-center gap-2 px-6 py-2 rounded transition ${favoriteButtonClass} text-white`}
            >
              {isFavorited ? (
                <>
                  <Heart className="mr-1" size={20} fill="white" /> Favorilerden Çıkar
                </>
              ) : (
                <>
                  <Heart className="mr-1" size={20} fill="none" /> Favorilere Ekle
                </>
              )}
            </button>
          </div>
        </div>
      </div>

      {/* Sepete ekleme animasyonu */}
      <AnimatePresence>
        {flyingImage && (
          <motion.img
            key="flying-img"
            src={flyingImage.src}
            initial={{
              position: "fixed",
              top: flyingImage.y,
              left: flyingImage.x,
              width: flyingImage.width,
              height: flyingImage.height,
              borderRadius: 8,
              zIndex: 1000,
              pointerEvents: "none",
            }}
            animate={{
              top: cartIconPosition.y,
              left: cartIconPosition.x,
              width: 40,
              height: 40,
              opacity: 0.8,
            }}
            transition={{ duration: 0.8, ease: "easeInOut" }}
            onAnimationComplete={onAnimationComplete}
            style={{ borderRadius: 8 }}
          />
        )}
      </AnimatePresence>

      {/* İlgili Ürünler Bölümü */}
      <div className="mt-12">
        <h2 className="text-3xl font-bold mb-6 text-center">İlgili Ürünler</h2>
        {loadingRelated ? (
          <p className="text-center">İlgili ürünler yükleniyor...</p>
        ) : errorRelated ? (
          <p className="text-danger text-center">{errorRelated}</p>
        ) : relatedProducts.length > 0 ? (
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {relatedProducts.map((p) => (
              <Link
                to={`/products/${p._id}`}
                key={p._id}
                className={`block rounded-lg shadow-custom-light overflow-hidden ${cardBgClass} ${cardTextClass} ${relatedCardHoverClass}`}
              >
                <img
                  src={p.image || "https://via.placeholder.com/400x300?text=Ürün+Görseli+Mevcut+Değil"}
                  alt={p.name}
                  className="w-full h-48 object-cover"
                />
                <div className="p-4">
                  <h3 className="text-lg font-semibold mb-2">{p.name}</h3>
                  <p className="text-muted text-sm mb-3 line-clamp-2">{p.description}</p>
                  {p.discountPrice ? (
                    <div className="flex items-baseline mb-2">
                      <span className="text-xl font-bold text-green-500 mr-2">{p.discountPrice} TL</span>
                      <span className="text-sm line-through text-red-500">{p.price} TL</span>
                    </div>
                  ) : (
                    <p className="text-xl font-bold text-primary mb-2">{p.price} TL</p>
                  )}
                  <button
                    onClick={(e) => {
                      e.preventDefault();
                      addToCart(p);
                    }}
                    className={`mt-3 w-full ${buttonClass}`}
                  >
                    Sepete Ekle
                  </button>
                </div>
              </Link>
            ))}
          </div>
        ) : (
          <p className="text-center text-muted">İlgili ürün bulunamadı.</p>
        )}
      </div>
    </div>
  );
};

export default ProductDetail;
